package chemgl.smileit.domaindata class MoleculeDetails(    val name: String,    val smile: String,    val atoms: List<AtomPosition>,    val hasImplicitHydrogens: Boolean,    val substitutionSites: List<Int>)data class AtomPosition(    val atomIndex: Int,    val x: Double,    val y: Double)data class SynthesisRequest(    val mainMolecule: MoleculeDetails,    val substituents: List<MoleculeDetails>)data class SynthesizedMolecule(    val mainMolecule: MoleculeDetails,    val substitutionDetails: List<SubstitutionDetail>,    val resultingMolecule: MoleculeDetails)data class SubstitutionDetail(    val mainAtom: AtomPosition,    val substituentMolecule: MoleculeDetails,    val substituentAtom: AtomPosition)