package chemgl.smileit.domainimport java.math.BigDecimaldata class MoleculeDetails(    val name: String,    val smile: String,    val atoms: List<AtomPosition>,    val hasImplicitHydrogens: Boolean,    val substitutionSites: List<Int>)data class AtomPosition(    val atomIndex: Int,    val x: BigDecimal,    val y: BigDecimal,    val smile: String,)data class SynthesisRequest(    val mainMolecule: MoleculeDetails,    val substitutes: List<MoleculeDetails>,    val nSubstitutes: Int)data class SynthesizedMolecule(    val mainMolecule: MoleculeDetails,    val substitutionDetails: List<SubstitutionDetail>,    val resultingMolecule: MoleculeDetails,)data class SubstitutionDetail(    val mainAtom: AtomPosition,    val substituentMolecule: MoleculeDetails,    val substituentAtom: AtomPosition)